import React from "react";
import { Link } from "react-router-dom";
import locations from "./constants/HomeConstants";
import './Button.css';

const STYLES = ['btn--primary', 'btn--outline'];
const SIZES = ['btn--medium', 'btn--large'];

export const Button = ({children, type, location, onClick, buttonStyle, buttonSize}) =>{
    const checkButtonStyle = STYLES.includes(buttonStyle) ? buttonStyle : STYLES[0];
    const checkButtonSize = SIZES.includes(buttonSize) ? buttonSize : SIZES[0];

    if(!location){
        let location = locations.HOME_LOCATION;
    }

    const handleClick = () => {
        window.open(location);
    };

    return (
        <Link to={location} className="btn-mobile">
            <button className={`btn ${checkButtonStyle} ${checkButtonSize}`} 
                onClick={onClick}
                location={location} type={type}>
                    <a href={location} target="_blank"></a>
                    {children}
            </button>
        </Link>
    )
}
